import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import JobCard from './JobCards/JobCard';
import { useSelector, useDispatch } from 'react-redux';
import Pagination from './Pagination'


const MainSection = styled.main`
width:100%;
height:100%;
display:grid;
place-items:center;`
const Container = styled.div`
width:90%;
padding: 100px 50px;
display: grid;
grid-gap: 1rem;
grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));`


const Main = ({ jobs, error, loading }) => {
    const dispatch = useDispatch()
    const [jobData, setJobs] = useState([])
    const { newSearchLoading, filtered } = useSelector(state => state.jobs)

    useEffect(() => {
        setJobs(jobs)
    }, [jobs])

    useEffect(() => {
        if (newSearchLoading) {
            setJobs([])
        }

    }, [newSearchLoading])


    useEffect(() => {
        setJobs(filtered)
    }, [filtered])
    return <MainSection>
        <Pagination />
        <Container>
            {error ? (<h2>Error</h2>) :
                loading || newSearchLoading ? (<h2> Loading ...</h2>) :
                    jobData && jobData.map(job => (<JobCard className="job-card" key={index} data={job} />))
            }

        </Container>
    </MainSection>
}

export default Main;
