import styled from 'styled-components';

const Button = styled('button')`
background: var(--primary);
color: white;
font-size:inherit;
font-family:inherit;
font-weight:500;
outline:none;
border:none;
height: 28px;
line-height:28px;
padding: 0 28px;
border-radius: 12px;

&:hover{
filter: brightness(1.1);
}`
export default Button;
