import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import JobCard from './JobCards/JobCard';
import { useSelector } from 'react-redux';

const MainSection = styled.main`
width:100%;
height:100%;
display:grid;
place-items:center;`
const Container = styled.div`
width:90%;
padding: 100px 50px;
display: grid;
grid-gap: 1rem;
grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));`


const Main = () => {
    useEffect(() => {
        setJobs(res.filteredJobs)
    }, [res])

    const [filteredJobs, setJobs] = useState([])

    const res = useSelector(state => state.jobs)
    setJobs(res.filteredJobs);



    return <MainSection>
        <Container>
            {/* {loading ? (<h2> Loading ...</h2>)
                : jobs.map(job => <JobCard key={job.id} data={job}></JobCard>)} */}
            {filteredJobs && filteredJobs.map(job => <JobCard key={job.id} data={job}></JobCard>)}
        </Container>
    </MainSection>
}


export default Main;
