import { Fragment, useState, useEffect } from 'react';
import MetaHead from '../src/components/Head';
import Layout from '../src/layout/Layout';
import NavBar from '../src/components/NavBar';
import Main from '../src/components/Main';
import Footer from '../src/components/Footer'
import axios from 'axios'

const Index = ({ jobs }) => {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);
    const [latestJobs, setLatestJob] = useState([]);

    useEffect(() => {
        setLoading(false)
        setLatestJob(jobs)
    }, [jobs])

    return (
        <Fragment>
            <MetaHead />
            <Layout>
                <NavBar />

                <Main jobs={latestJobs} error={error} loading={loading} />
                <Footer />


            </Layout>
        </Fragment>)
}

Index.getStaticProps = async () => {
    const res = await axios.get("https://jobs.github.com/positions.json?page=0");
    try {
        return {
            props: {
                jobs: res.data,
            }
        }
    } catch (error) {
        console.log(error)
    }
}

export default Index;
