import { useSelector, useDispatch } from 'react-redux';
import { clear_filter } from '../store/actions/query';

const Pagination = () => {
    const dispatch = useDispatch()
    const { search, location } = useSelector(state => state.query);

    return <div>     {search && (<p> You search for {search}
    </p>)}
        {location && (<p> Jobs in {location}</p>)}
        {search || location ? (<p onClick={dispatch(clear_filter())}>Clear Filter</p>) : null}</div>
}

export default Pagination
