import { useState, useEffect } from 'react'

const UserArea = ({ isExpand }) => {
    const [expand, setExpand] = useState(null)
    useEffect(() => {
        if (isExpand) {
            setExpand("expand")
        } else {
            setExpand(null)
        }
    }, [isExpand])
    return <div className={`user-area-modal ${expand}`}> Hi</div>
}

export default UserArea
