import styled from 'styled-components';

import CompanyLogo from './CompanyLogo';
import SecondaryText from './SecondayText';


const JobCard = ({ data }) => {

    const JobCard = styled.div`
background: #fff;
box-shadow: 5px 5px 20px rgba(0,0,0,0.05);
border-radius: 24px;
min-height:300px;
max-width:350px;
place-items:center;
padding: 20px 30px;`

    const CompanyTitle = styled(SecondaryText)`
margin: 5px 0 ;
`

    const JobTitle = styled.h2`
font-size:16px;
line-height:20px;
font-weight: 700;

`

    return <JobCard>
        <CompanyLogo>
            <img src={data.company_logo} alt={data.company} />
        </CompanyLogo>
        <CompanyTitle>
            {data.company}
        </CompanyTitle>
        <JobTitle>
            {data.title}
        </JobTitle>


    </JobCard>
}

export default JobCard;
