import axios from 'axios'
import { SET_JOBRES, FETCHERROR, INIT_NEW_SEARCH } from './types'

export const newSearch = () => {
    return {
        type: INIT_NEW_SEARCH,
    }
}

export const getData = (searchQuery, locationQuery, page = 1) => async dispatch => {
    let res;
    if (searchQuery === null & location === null) {
        return getLatestJob(page)
    } else {
        res = await axios.get(`https://cors-anywhere.herokuapp.com/https://jobs.github.com/positions.json?title=${searchQuery}&location=${locationQuery}&page=${page}`)
    }

    try {

        dispatch({
            type: SET_JOBRES,
            payload: res.data
        })

    }
    catch (error) {
        dispatch({
            type: FETCHERROR,
            payload: error
        })
    }
}


export const getLatestJob = (page) => async dispatch => {

    const res = await axios.get(`https://cors-anywhere.herokuapp.com/https://jobs.github.com/positions.json?page=${page}`);
    try {

        dispatch({
            type: SET_JOBRES,
            payload: res.data
        })

    }
    catch (error) {
        dispatch({
            type: FETCHERROR,
            payload: error
        })
    }
}
