import styled from 'styled-components';
import { useState, useEffect } from 'react'


const Input = styled('input')`

outline: none;
height:28px;
line-height: 28px;
padding: 0 8px;
border-radius: 4px;
font-size: inherit;
font-family:inherit;
border: 1px solid var(--lightText);
margin: 0 10px;
width: 200px;


`

const Button = styled('button')`
background: var(--primary);
color: white;
font-size:inherit;
font-family:inherit;
font-weight:500;
outline:none;
border:none;
height: 28px;
line-height:28px;
padding: 0 28px;
border-radius: 12px;

&:hover{
filter: brightness(1.1);
}`

const SearchBar = () => {
    const [query, setQuery] = useState({
        searchQuery: "",
        locationQuery: ""
    })

    const { searchQuery, locationQuery } = query


    const onChangeHandler = (e) => {
        setQuery({ ...query, [e.target.name]: e.target.value })
    }

    useEffect(() => {
        console.log(searchQuery)
    }, [searchQuery])

    const submitHandler = (e) => {
        e.preventDefault()
        setQuery({
            searchQuery: "",
            locationQuery: ""
        })
    }


    return (
        <form onSubmit={submitHandler}>

            <Input type="text" placeholder="Job Title, Company name" name="searchQuery"
                value={searchQuery}
                onChange={(e) => onChangeHandler(e)} />
            <Input type="text" placeholder="Location, City, Country" name="locationQuery"
                value={locationQuery}
                onChange={(e) => onChangeHandler(e)} />

            <Button type="submit">Search</Button>
        </form>
    )
}

export default SearchBar;
