import { useEffect } from "react"

const JobDetailsPage = ({ id, data }) => {
    useEffect(() => {
        const history = JSON.parse(localStorage.getItem('broswedJob') || []);
        const updatedHistory = history.concat(id);
        localStorage.setItem('browsedJob', JSON.stringify(updatedHistory))

    }, [id])
    return <div> {data && (<div><p>You are looking at jobs with id: {id}</p>
        <p> Company : {data.company}</p>
        <p> Title : {data.title}</p>
        <p> Description: {data.description}</p></div>)}
    </div>
}

export default JobDetailsPage
