import { SET_FILTER_OPTIONS, CLEAR_FILTER } from '../actions/types'

const initialState = {
}

export default function (state = initialState, action) {
    const { type, payload } = action

    switch (type) {
        case SET_FILTER_OPTIONS:
            return {
                search: payload.search,
                location: payload.location
            }
        case CLEAR_FILTER:
            return {
                search: null,
                location: null
            }
        default:
            return state;
    }

}
