import styled from 'styled-components';
import { useState } from 'react'

const SearchBar = () => {
    const [searchQuery, setSearchQuery] = useState("");
    const [locationQuery, setLocationQuery] = useState("")

    const Input = styled.input`
    outline: none;
    height:28px;
    line-height: 28px;
    padding: 0 8px;
    border-radius: 4px;
    font-size: inherit;
    font-family:inherit;
    border: 1px solid var(--lightText);
    margin: 0 10px;
    width: 200px;

    &:focus{
        border: 1px solid var(--primary);
    }
    `

    const Button = styled.button`
    background: var(--primary);
    color: white;
    font-size:inherit;
    font-family:inherit;
    font-weight:500;
    outline:none;
    border:none;
    height: 28px;
    line-height:28px;
    padding: 0 28px;
    border-radius: 12px;

    &:hover{
        filter: brightness(1.1);
    }`

    const onChangeHandler = (e) => {
        e.preventDefault()
        if (e.target.name === "searchQuery") {
            return setSearchQuery(e.target.value)
        } else if (e.target.name === "location") {
            return setLocationQuery(e.target.value)
        }
    }


    return (
        <form onSubmit>
            <Input type="text" placeholder="Job Title, Company name" name="searchQuery"
                value={searchQuery}
                onChange={(e) => onChangeHandler(e)} />
            <Input type="text" placeholder="Location, City, Country" name="location"
                value={locationQuery}
                onChange={(e) => onChangeHandler(e)} />
            <Button type="submit">Search</Button>
        </form>)
}

export default SearchBar;
