import { SET_FILTER_OPTIONS, CLEAR_FILTER } from './types'

export const set_filter = (search, location) => {
    return {
        type: SET_FILTER_OPTIONS,
        payload: {
            search,
            location,
        }
    }
}

export const clear_filter = () => {
    return {
        type: CLEAR_FILTER,
    }
}
