import { SET_JOBRES, INIT_NEW_SEARCH } from "../actions/types"

const initialState = {

}

export default function (state = initialState, action) {
    const { type, payload } = action

    switch (type) {
        case SET_JOBRES:
            return {
                loading: false,
                filtered: payload,
            }
        case INIT_NEW_SEARCH:
            return {
                filtered: [],
                loading: true
            }
        default:
            return state;
    }

}
