import axios from 'axios'
import { SET_JOBRES, FETCHERROR } from './types'

export const newSearch = () => {
    return {
        type: NEW_SEARCH,
    }
}

export const getData = (searchQuery, locationQuery, page = 0) => async dispatch => {
    const res = await axios.get(`https://cors-anywhere.herokuapp.com/https://jobs.github.com/positions.json?title=${searchQuery}&location=${locationQuery}&page=${page}`)
    try {

        dispatch({
            type: SET_JOBRES,
            payload: res.data
        })
        console.log(res.data)
        console.log(`https://cors-anywhere.herokuapp.com/https://jobs.github.com/positions.json?description=${searchQuery}&location=${locationQuery}&page=${page}`)
    }
    catch (error) {
        dispatch({
            type: FETCHERROR,
            payload: error
        })
    }
}
