import styled from 'styled-components';

import CompanyLogo from './CompanyLogo';
import SecondaryText from './SecondayText';

import '@fortawesome/fontawesome-free/js/fontawesome';
import '@fortawesome/fontawesome-free/js/solid';

const JobCard = ({ data }) => {

    const JobCard = styled.div`
background: #fff;
box-shadow: 5px 5px 20px rgba(0,0,0,0.05);
border-radius: 24px;
height:200px;
max-width:350px;
padding: 20px 30px;
display:grid;
grid-template: "Logo Logo" 50px
"Company Company" 18px
"Title Title" 1fr
"Location Time" 50px / 1fr 1fr;
`

    const CompanyTitle = styled(SecondaryText)`
grid-area: Company
`

    const JobTitle = styled.a`
font-size:16px;
line-height:20px;
font-weight: 700;
grid-area: Title;
color: #000;
text-decoration: none;

&:hover{
    text-decoration: underline;
}

`

    const calculateTime = (createdAt) => {
        const now = new Date()
        const difference = now - new Date(createdAt)
        return (Math.ceil(difference / 1000 / 60 / 60 / 24))

    }

    const Location = styled(SecondaryText)`
    grid-area: Location
    text-align:left`
    const Time = styled(SecondaryText)`
    grid-area: Time;
    text-align:right;`

    const Icon = styled.i`
    margin-right:6px;`

    return <JobCard>
        <CompanyLogo>
            <img src={data.company_logo} alt={data.company} />
        </CompanyLogo>
        <CompanyTitle>
            {data.company}
        </CompanyTitle>
        <JobTitle href={data.url}>
            {data.title}
        </JobTitle>
        <Location>
            <Icon className="fas fa-map-marker-alt" />{data.location}
        </Location>
        <Time>
            {calculateTime(data.created_at)} days ago
        </Time>


    </JobCard>
}

export default JobCard;
