import { SET_FILTER_OPTIONS, CLEAR_FILTER, SET_JOBRES, FETCHERROR } from './types'
import axios from 'axios'

export const set_filter = (search, location) => {
    return {
        type: SET_FILTER_OPTIONS,
        payload: {
            search,
            location,
        }
    }
}

export const clear_filter = () => async dispatch => {
    dispatch({
        type: CLEAR_FILTER
    })
    const res = await axios.get("https://cors-anywhere.herokuapp.com/https://jobs.github.com/positions.json?page=1");

    try {
        dispatch({
            type: SET_JOBRES,
            payload: res.data
        })
    }
    catch (error) {
        dispatch({
            type: FETCHERROR,
        })
    }
}
