import { useSelector, useDispatch } from 'react-redux';
import { clear_filter, set_page } from '../store/actions/query';
import { getData, getLatestJob, newSearch } from '../store/actions/jobs';
import { useEffect, useState } from 'react';

const Pagination = () => {

    const dispatch = useDispatch()
    const { search, location, page, filteredSearch } = useSelector(state => state.query);
    const [hasPrev, setHasPrev] = useState(false)
    const [hasNext, setHasNext] = useState(true)

    useEffect(() => {
        if (page <= 1) { setHasPrev(false) } else {
            setHasPrev(true)
        }
    }, [page])

    const PaginationHandler = () => {
        dispatch(newSearch())
        if (filteredSearch) {
            dispatch(getData(search, location, page + 1))
        } else {
            dispatch(getLatestJob(page + 1))
            dispatch(set_page(page + 1))
        }
    }
    return <div className="pagination-row">
        {search && (<p className="pagination-search"> You search for {search} </p>)}
        {location && (<p className="pagination-location"> Jobs in {location}</p>)}
        {search || location ? (<p className="pagination-clear" onClick={() => dispatch(clear_filter())}>Clear Filter</p>) : null}
        {hasPrev && (<span className="pagination-prev"> Prev </span>)}
        {hasNext && (<span onClick={PaginationHandler} className="pagination-next"> Next </span>)}
    </div>
}

export default Pagination
