import axios from 'axios'
import { SET_JOBRES, FETCHERROR } from './types'

export const getData = (searchQuery, locationQuery, page = 0) => async dispatch => {
    try {
        const res = await axios.get(`https://cors-anywhere.herokuapp.com/https://jobs.github.com/positions.json?description=${searchQuery}&location=${locationQuery}&page=${page}`)
        dispatch({
            type: SET_JOBRES,
            payload: res.data
        })
        console.log(res.data)
    }
    catch (error) {
        dispatch({
            type: FETCHERROR,
            payload: error
        })
    }
}
