import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import JobCard from './JobCards/JobCard';
import { useSelector, useDispatch } from 'react-redux';
import Pagination from './Pagination'



const Main = ({ jobs, error, loading }) => {
    const dispatch = useDispatch()
    const [jobData, setJobs] = useState([])
    const { newSearchLoading, filtered } = useSelector(state => state.jobs)

    useEffect(() => {
        setJobs(jobs)
    }, [jobs])

    useEffect(() => {
        if (newSearchLoading) {
            setJobs([])
        }

    }, [newSearchLoading])


    useEffect(() => {
        setJobs(filtered)
    }, [filtered])
    return <main>
        <Pagination />
        <div className="container">
            {error ? (<h2>Error</h2>) :
                loading || newSearchLoading ? (<h2> Loading ...</h2>) :
                    jobData && jobData.map(job => (<JobCard className="job-card" key={job.id} data={job} />))
            }

        </div>
    </main>
}

export default Main;
