import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import JobCard from './JobCards/JobCard';
import { useSelector } from 'react-redux';


const MainSection = styled.main`
width:100%;
height:100%;
display:grid;
place-items:center;`
const Container = styled.div`
width:90%;
padding: 100px 50px;
display: grid;
grid-gap: 1rem;
grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));`


const Main = ({ jobs, error, loading }) => {
    const [jobData, setJobs] = useState([])
    const { newSearchLoading, filtered } = useSelector(state => state.jobs)
    const { search, location } = useSelector(state => state.query)
    useEffect(() => {
        setJobs(jobs)
    }, [jobs])

    useEffect(() => {
        if (newSearchLoading) {
            setJobs([])
        }

    }, [newSearchLoading])


    useEffect(() => {
        setJobs(filtered)
    }, [filtered])
    return <MainSection>
        <Container>
            {search && (<p> You search for {search}
            </p>)}
            {location && (<p> Jobs in {location}</p>)}
            {search || location ? (<p>Clear Filter</p>) : null}
            {error ? (<h2>Error</h2>) :
                loading || newSearchLoading ? (<h2> Loading ...</h2>) :
                    jobData && jobData.map((job, index) => {
                        if (index <= 9) {
                            console.log(index)
                                (

                                    <JobCard key={job.id} data={job}></JobCard>)
                        }
                    })}

        </Container>
    </MainSection>
}

export default Main;
