import { Fragment, useState, useEffect } from 'react';
import MetaHead from '../src/components/Head';
import Layout from '../src/layout/Layout';
import NavBar from '../src/components/NavBar';
import Main from '../src/components/Main';
import Footer from '../src/components/Footer'
import axios from 'axios'

const Index = (props) => {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);
    const [latestJobs, setLatestJob] = useState([]);

    useEffect(() => {
        getJobs()
    }, [])

    const getJobs = async () => {
        const res = await axios.get("https://cors-anywhere.herokuapp.com/https://jobs.github.com/positions.json?page=0");
        try {
            setLatestJob(res.data)
            setLoading(false)
        } catch{
            setError(true)
        }
    }

    return (
        <Fragment>
            <MetaHead />
            <Layout>
                <NavBar />

                <Main jobs={latestJobs} error={error} loading={loading} />
                <Footer />


            </Layout>
        </Fragment>)
}


export default Index;
