import styled from 'styled-components';

import SecondaryText from './SecondayText';

import '@fortawesome/fontawesome-free/js/fontawesome';
import '@fortawesome/fontawesome-free/js/solid';



const calculateTime = (createdAt) => {
    const now = new Date()
    const difference = now - new Date(createdAt)
    return (Math.ceil(difference / 1000 / 60 / 60 / 24))

}


const Time = styled(SecondaryText)`
    grid-area: Time;
    text-align:right;`



const JobCard = ({ data }) => {


    return <div className="job-card">

        <div className="companny-logo">
            <a href={data.company_url}> <img src={data.company_logo} alt={data.company} target="_blank" /></a>
        </div>

        <a className="company-title" href={data.company_url} target="_blank">
            {data.company}
        </a>
        <a className="job-title" href={data.url} target="_blank">
            {data.title}
        </a>
        <p className="location-text">
            <i className="fas fa-map-marker-alt icon-margin" />{data.location}
        </p>
        <p className="time-text">
            {calculateTime(data.created_at)} day(s) ago
        </p>
    </div>
}

export default JobCard;
