        localStorage.setItem("BrowsedJob", { newHistory })

    }, [id])
    return <div> {data && (<div><p>You are looking at jobs with id: {id}</p>
        <p> Company : {data.company}</p>
        <p> Title : {data.title}</p>
        <p> Description: {data.description}</p></div>)}
    </div>
}

